package fundamental;

public class RelationalLogicalOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         int a=10,b=10;
//relational
         System.out.println(a>b);
         System.out.println(a==b);
         //logical
         System.out.println(true&&false);
         System.out.println(true||false);
         System.out.println(!false);
	}

}
